import React from "react";
import Axios from "axios";
import "./App.css";
function Card(props) {
  return (
    <div className="Card">
      <label>Movie Name:</label>
      <h3>{props.movieName}</h3>
      <label>Review:</label>
      <h3>{props.movieReview}</h3>
      <button
        onClick={() => {
          Axios.delete(`http://localhost:3001/api/delete/${props.id}`);
        }}
      >
        Delete
      </button>
      <input type="text" name="update" />
      <button onClick={()=>{
        Axios.put("")
      }}>
        Update
      </button>
    </div>
  );
}

export default Card;
